import React, { memo, useMemo } from 'react';
import '../styles/GameBoard.css';

const GameBoard = memo(({ 
  currentRound, 
  currentPhase,
  selectedLocation, 
  onLocationSelect, 
  players, 
  selectedAction, 
  targetPlayer, 
  onTargetSelect 
}) => {
  // ÏÑúÏãùÏßÄ Î™©Î°ùÍ≥º ÏïÑÏù¥ÏΩò - useMemoÎ°ú ÏµúÏ†ÅÌôî
  const habitats = useMemo(() => [
    { id: 'Í∞ï', name: 'Í∞ï', icon: 'üåä', className: 'habitat-river' },
    { id: 'ÌïòÎäò', name: 'ÌïòÎäò', icon: '‚òÅÔ∏è', className: 'habitat-sky' },
    { id: 'Îì§', name: 'Îì§', icon: 'üåæ', className: 'habitat-field' },
    { id: 'Ïà≤', name: 'Ïà≤', icon: 'üå≥', className: 'habitat-forest' }
  ], []);

  // Ïï°ÏÖòÏóê Îî∞Î•∏ UI Î†åÎçîÎßÅ
  const actionUI = useMemo(() => {
    if (!selectedAction) {
      return (
        <div className="board-message fade-in">
          <div className="message-icon">‚ùì</div>
          <p>ÌñâÎèôÏùÑ ÏÑ†ÌÉùÌï¥Ï£ºÏÑ∏Ïöî.</p>
        </div>
      );
    }

    if (selectedAction === 'move') {
      return (
        <div className="habitat-selection fade-in">
          <div className="section-header">
            <div className="section-icon">üö∂</div>
            <h3>Ïù¥ÎèôÌï† ÏÑúÏãùÏßÄÎ•º ÏÑ†ÌÉùÌïòÏÑ∏Ïöî</h3>
          </div>
          <div className="habitat-grid">
            {habitats.map(habitat => (
              <div 
                key={habitat.id}
                className={`habitat-card ${habitat.className} ${selectedLocation === habitat.id ? 'selected' : ''}`}
                onClick={() => onLocationSelect(habitat.id)}
              >
                <div className="habitat-icon">{habitat.icon}</div>
                <h4>{habitat.name}</h4>
                <div className="habitat-info">
                  <span className="player-count">
                    ÌòÑÏû¨ ÌîåÎ†àÏù¥Ïñ¥: {players.filter(p => p.location === habitat.id).length}Î™Ö
                  </span>
                </div>
              </div>
            ))}
          </div>
        </div>
      );
    }

    if (selectedAction === 'attack') {
      const targetablePlayers = players.filter(p => p.id !== '1' && p.location === selectedLocation);

      return (
        <div className="player-selection fade-in">
          <div className="section-header">
            <div className="section-icon">‚öîÔ∏è</div>
            <h3>Í≥µÍ≤©Ìï† ÌîåÎ†àÏù¥Ïñ¥Î•º ÏÑ†ÌÉùÌïòÏÑ∏Ïöî</h3>
          </div>

          {targetablePlayers.length > 0 ? (
            <div className="player-grid">
              {targetablePlayers.map(player => (
                <div 
                  key={player.id}
                  className={`player-card ${targetPlayer && targetPlayer.id === player.id ? 'selected' : ''}`}
                  onClick={() => onTargetSelect(player)}
                >
                  <div className="player-avatar">{player.name.charAt(0).toUpperCase()}</div>
                  <div className="player-info">
                    <h4>{player.name}</h4>
                    <p className="player-animal">ÎèôÎ¨º: {player.animal || '???'}</p>
                  </div>
                  <div className="action-indicator">
                    {targetPlayer && targetPlayer.id === player.id && <span className="attack-icon">‚öîÔ∏è</span>}
                  </div>
                </div>
              ))}
            </div>
          ) : (
            <div className="empty-state">
              <div className="empty-icon">üîç</div>
              <p className="no-players-message">Ïù¥ ÏÑúÏãùÏßÄÏóêÎäî Îã§Î•∏ ÌîåÎ†àÏù¥Ïñ¥Í∞Ä ÏóÜÏäµÎãàÎã§.</p>
            </div>
          )}
        </div>
      );
    }

    if (selectedAction === 'peek') {
      const peekablePlayers = players.filter(p => p.id !== '1');

      return (
        <div className="player-selection fade-in">
          <div className="section-header">
            <div className="section-icon">üëÅÔ∏è</div>
            <h3>ÏóøÎ≥º ÌîåÎ†àÏù¥Ïñ¥Î•º ÏÑ†ÌÉùÌïòÏÑ∏Ïöî</h3>
          </div>

          {peekablePlayers.length > 0 ? (
            <div className="player-grid">
              {peekablePlayers.map(player => (
                <div 
                  key={player.id}
                  className={`player-card ${targetPlayer && targetPlayer.id === player.id ? 'selected' : ''}`}
                  onClick={() => onTargetSelect(player)}
                >
                  <div className="player-avatar">{player.name.charAt(0).toUpperCase()}</div>
                  <div className="player-info">
                    <h4>{player.name}</h4>
                    {targetPlayer && targetPlayer.id === player.id && targetPlayer.peekedAnimal ? (
                      <p className="player-animal revealed">ÎèôÎ¨º: {targetPlayer.peekedAnimal}</p>
                    ) : (
                      <p className="player-animal hidden">ÎèôÎ¨º: ???</p>
                    )}
                  </div>
                  <div className="action-indicator">
                    {targetPlayer && targetPlayer.id === player.id && <span className="peek-icon">üëÅÔ∏è</span>}
                  </div>
                </div>
              ))}
            </div>
          ) : (
            <div className="empty-state">
              <div className="empty-icon">üîç</div>
              <p className="no-players-message">ÏóøÎ≥º Ïàò ÏûàÎäî ÌîåÎ†àÏù¥Ïñ¥Í∞Ä ÏóÜÏäµÎãàÎã§.</p>
            </div>
          )}
        </div>
      );
    }

    return null;
  }, [selectedAction, habitats, selectedLocation, players, targetPlayer, onLocationSelect, onTargetSelect]);

  return (
    <div className="game-board-container">
      <div className="game-board-header">
        <h2>Í≤åÏûÑ Î≥¥Îìú</h2>
        <div className="round-indicator">
          <div className="round-icon">üîÑ</div>
          <span>ÎùºÏö¥Îìú: {currentRound}/4</span>
        </div>
      </div>

      <div className="game-board-content">
        {actionUI}
      </div>

      <div className="game-board-footer">
        <div className="location-info">
          {selectedLocation && (
            <div className={`current-location ${habitats.find(h => h.id === selectedLocation)?.className}`}>
              <span className="location-label">ÌòÑÏû¨ ÏúÑÏπò:</span>
              <span className="location-value">
                {habitats.find(h => h.id === selectedLocation)?.icon} {selectedLocation}
              </span>
            </div>
          )}
        </div>
      </div>
    </div>
  );
});

export default GameBoard;
